import "./Campo.css";

const Campo = ({
  type= 'text',
  label,
  placeholder,
  valor,
  aoAlterado,
  obrigatorio = false,
}) => {
  const placeholdModificada = `${placeholder}...`;

  const aoDigitado = (evento) => {
    aoAlterado(evento.target.value);
  };

  return (
    <div className={`campo campo-${type}`}>
      <label>{label}</label>
      <input
        type={type}
        value={valor}
        onChange={aoDigitado}
        required={obrigatorio}
        placeholder={placeholdModificada}
      />
    </div>
  );
};

export default Campo;
